import { useState } from 'react';

export default initialValue => {
  const [songs, setTodos] = useState(initialValue);

  return {
    songs,
    addTodo: todoText => {
      setTodos([...todos, todoText]);
    },
    deleteTodo: todoIndex => {
      const newTodos = todos.filter((_, index) => index !== todoIndex);

      setTodos(newTodos);
    }
  };
};
